mapper.select:
SELECT s.id, s.name, s.restaurant_id as restaurantId, s.customer_num as customerNum FROM seat s where restaurant_id = #{restaurantId}
and (s.name like concat('%',#{key},'%') or s.customer_num like concat('%',#{key},'%'))
order by
s.${page.sortName} ${page.sortOrder},
s.id desc LIMIT #{page.start} , #{page.pageSize}

mapper.update:
UPDATE seat
name = #{name}, customer_num = #{customerNum},
WHERE id = #{id} and restaurant_id = #{restaurantId}
