mapper.select:
select u.id, u.account, u.name, u.password, role.name 'roleName' from user u LEFT JOIN role on role.id = u.role_id where u.restaurant_id = #{restaurantId} and u.role_id not in ${notInRoleIdString}
and(u.account like concat('%',#{key},'%') or u.name like concat('%',#{key},'%'))
ORDER BY u.id

mapper.update:
UPDATE user
name = #{name}, password = #{password}, role_id = #{roleId}, phone = #{phone},
WHERE id = #{id}
